// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import CreatePackage from './CreatePackage';
import ViewMyPackages from './ViewMyPackages';
import EditPackage from './EditPackage';

function App() {
  const [packages, setPackages] = useState([]);

  // Add package
  const addPackageToList = (newPackage) => {
    setPackages((prevPackages) => [...prevPackages, { id: Date.now(), ...newPackage }]);
  };

  // Delete package
  const deletePackageFromList = (id) => {
    setPackages((prevPackages) => prevPackages.filter((pkg) => pkg.id !== id));
  };

  // Update package
  const updatePackageInList = (updatedPackage) => {
    setPackages((prevPackages) =>
      prevPackages.map((pkg) => (pkg.id === updatedPackage.id ? updatedPackage : pkg))
    );
  };

  return (
    <Router>
      <div className="App">
        <h1>Courier Management System</h1>
        <nav style={styles.nav}>
          <ul style={styles.ul}>
            <li style={styles.li}>
              <Link to="/" style={styles.link}>Create Package</Link>
            </li>
            <li style={styles.li}>
              <Link to="/view-my-packages" style={styles.link}>View My Packages</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route
            path="/"
            element={<CreatePackage addPackageToList={addPackageToList} />}
          />
          <Route
            path="/view-my-packages"
            element={
              <ViewMyPackages
                packages={packages}
                deletePackage={deletePackageFromList}
              />
            }
          />
          <Route
            path="/edit-package/:id"
            element={
              <EditPackage
                packages={packages}
                updatePackage={updatePackageInList}
              />
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

const styles = {
  nav: {
    backgroundColor: '#333',
    padding: '10px',
  },
  ul: {
    listStyleType: 'none',
    display: 'flex',
    gap: '15px',
    justifyContent: 'center',
  },
  li: {
    display: 'inline',
  },
  link: {
    color: 'white',
    textDecoration: 'none',
  },
};

export default App;
