import React, { useState } from 'react';
import axios from 'axios';

const CreatePackage = ({ addPackageToList }) => {
  const [packageData, setPackageData] = useState({
    deliveryAddress: '',
    payOnDelivery: false,
    status: 'NEW',
    courierId: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setPackageData({
      ...packageData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8081/package/create-package', {
        ...packageData,
        courierId: packageData.courierId || null,
      });

      setMessage(`Package created successfully: ID ${response.data.id}`);
      if (addPackageToList) addPackageToList(response.data);

      setPackageData({
        deliveryAddress: '',
        payOnDelivery: false,
        status: 'NEW',
        courierId: '',
      });
    } catch (error) {
      setMessage('Failed to create package. Please try again.');
      console.error(error);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Create Package</h1>
      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.inputGroup}>
          <label htmlFor="deliveryAddress">Delivery Address:</label>
          <input
            type="text"
            id="deliveryAddress"
            name="deliveryAddress"
            value={packageData.deliveryAddress}
            onChange={handleChange}
            required
          />
        </div>
        <div style={styles.inputGroup}>
          <label>
            <input
              type="checkbox"
              name="payOnDelivery"
              checked={packageData.payOnDelivery}
              onChange={handleChange}
            />
            Pay on Delivery
          </label>
        </div>
        <div style={styles.inputGroup}>
          <label htmlFor="status">Status:</label>
          <select
            id="status"
            name="status"
            value={packageData.status}
            onChange={handleChange}
            required
          >
            <option value="NEW">NEW</option>
            <option value="PENDING">PENDING</option>
            <option value="DELIVERED">DELIVERED</option>
          </select>
        </div>
        <div style={styles.inputGroup}>
          <label htmlFor="courierId">Courier ID (Optional):</label>
          <input
            type="number"
            id="courierId"
            name="courierId"
            value={packageData.courierId}
            onChange={handleChange}
          />
        </div>
        <button type="submit" style={styles.button}>
          Create Package
        </button>
      </form>
      {message && <p style={styles.message}>{message}</p>}
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    maxWidth: '400px',
    margin: 'auto',
  },
  title: {
    textAlign: 'center',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
  },
  inputGroup: {
    display: 'flex',
    flexDirection: 'column',
  },
  button: {
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    padding: '10px',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  message: {
    marginTop: '10px',
    textAlign: 'center',
    color: '#d9534f',
  },
};

export default CreatePackage;
