import React, { useState } from 'react';
import axios from 'axios';

const CreatePackage = () => {
    const [packageData, setPackageData] = useState({
        deliveryAddress: '',
        payOnDelivery: false,
        status: 'NEW', // Status implicit
        courierId: '', // Opțional
    });
    const [message, setMessage] = useState('');

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setPackageData({
            ...packageData,
            [name]: type === 'checkbox' ? checked : value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8081/package/create-package', {
                ...packageData,
                courier: packageData.courierId ? { id: packageData.courierId } : null,
            });
            setMessage(`Package created successfully: ID ${response.data.id}`);
            setPackageData({
                deliveryAddress: '',
                payOnDelivery: false,
                status: 'NEW',
                courierId: '',
            });
        } catch (error) {
            console.error('Error creating package:', error);
            setMessage('Failed to create package. Please try again.');
        }
    };

    return (
        <div>
            <h1>Create Package</h1>
            <form onSubmit={handleSubmit}>
                {/* Delivery Address */}
                <div>
                    <label htmlFor="deliveryAddress">Delivery Address:</label>
                    <input
                        type="text"
                        id="deliveryAddress"
                        name="deliveryAddress"
                        value={packageData.deliveryAddress}
                        onChange={handleChange}
                        required
                    />
                </div>

                {/* Pay on Delivery */}
                <div>
                    <label htmlFor="payOnDelivery">
                        <input
                            type="checkbox"
                            id="payOnDelivery"
                            name="payOnDelivery"
                            checked={packageData.payOnDelivery}
                            onChange={handleChange}
                        />
                        Pay on Delivery
                    </label>
                </div>

                {/* Status */}
                <div>
                    <label htmlFor="status">Status:</label>
                    <select
                        id="status"
                        name="status"
                        value={packageData.status}
                        onChange={handleChange}
                        required
                    >
                        <option value="NEW">NEW</option>
                        <option value="PENDING">PENDING</option>
                        <option value="DELIVERED">DELIVERED</option>
                    </select>
                </div>

                {/* Courier ID */}
                <div>
                    <label htmlFor="courierId">Courier ID (Optional):</label>
                    <input
                        type="number"
                        id="courierId"
                        name="courierId"
                        value={packageData.courierId}
                        onChange={handleChange}
                    />
                </div>

                <button type="submit">Create Package</button>
            </form>

            {/* Message */}
            {message && <p>{message}</p>}
        </div>
    );
};

export default CreatePackage;
