import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const EditPackage = ({ packages, updatePackage }) => {
  const { id } = useParams();
  const navigate = useNavigate();

  // Găsim pachetul care trebuie editat
  const packageToEdit = packages.find((pkg) => pkg.id === parseInt(id, 10));

  if (!packageToEdit) {
    return <p style={styles.error}>Package not found!</p>;
  }

  const [formData, setFormData] = useState({
    name: packageToEdit.name || '',
    weight: packageToEdit.weight || '',
    destination: packageToEdit.destination || '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedPackage = { ...packageToEdit, ...formData };
    updatePackage(updatedPackage);
    alert('Package updated successfully!');
    navigate('/view-my-packages');
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Edit Package</h1>
      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.formGroup}>
          <label style={styles.label}>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            style={styles.input}
          />
        </div>
        <div style={styles.formGroup}>
          <label style={styles.label}>Weight:</label>
          <input
            type="text"
            name="weight"
            value={formData.weight}
            onChange={handleChange}
            style={styles.input}
          />
        </div>
        <div style={styles.formGroup}>
          <label style={styles.label}>Destination:</label>
          <input
            type="text"
            name="destination"
            value={formData.destination}
            onChange={handleChange}
            style={styles.input}
          />
        </div>
        <div style={styles.formActions}>
          <button type="submit" style={styles.saveButton}>Save</button>
          <button
            type="button"
            onClick={() => navigate('/view-my-packages')}
            style={styles.cancelButton}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

const styles = {
  // Aceleași stiluri
};

export default EditPackage;
