import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ViewMyPackages = () => {
  const [packages, setPackages] = useState([]);

  const fetchPackages = async () => {
    try {
      const response = await axios.get('http://localhost:8081/package/get-package');
      setPackages(response.data);
    } catch (error) {
      console.error('Error fetching packages:', error);
    }
  };

  useEffect(() => {
    fetchPackages();
  }, []);

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>View My Packages</h1>
      <button onClick={fetchPackages} style={styles.refreshButton}>
        Refresh
      </button>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.th}>Package ID</th>
            <th style={styles.th}>Courier Name</th>
            <th style={styles.th}>Created On</th>
            <th style={styles.th}>Delivery Address</th>
            <th style={styles.th}>Status</th>
          </tr>
        </thead>
        <tbody>
          {packages.map((pkg) => (
            <tr key={pkg.id} style={styles.row}>
              <td style={styles.td}>{pkg.id}</td>
              <td style={styles.td}>{pkg.courier ? pkg.courier.name : 'N/A'}</td>
              <td style={styles.td}>{pkg.createdOn}</td>
              <td style={styles.td}>{pkg.deliveryAddress}</td>
              <td style={styles.td}>{pkg.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '900px',
    margin: '40px auto',
    padding: '20px',
    backgroundColor: '#fdf7f0', // same as CreatePackage background color
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    textAlign: 'center',
  },
  title: {
    color: '#d4849a', // matching the color in CreatePackage
    fontSize: '24px',
    marginBottom: '20px',
  },
  refreshButton: {
    margin: '10px 0',
    padding: '10px',
    backgroundColor: '#d4849a', // matching the button color
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontWeight: 'bold',
    transition: 'background-color 0.3s',
  },
  table: {
    width: '80%',
    margin: 'auto',
    borderCollapse: 'collapse',
  },
  th: {
    backgroundColor: '#f4f4f4',
    border: '1px solid #ddd',
    padding: '10px',
    textAlign: 'left',
    color: '#6b615b', // matching the label color
  },
  td: {
    border: '1px solid #ddd',
    padding: '10px',
    color: '#6b615b', // matching the label color
  },
  row: {
    '&:hover': {
      backgroundColor: '#f1f1f1',
    },
  },
};

export default ViewMyPackages;
