import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


const ViewPackages = () => {
  const [packages, setPackages] = useState([]);
  const [editPackage, setEditPackage] = useState(null); // Stocăm pachetul care este în proces de editare
  const [formData, setFormData] = useState({
    id: '',
    courier: '',
    deliveryAddress: '',
    payOnDelivery: false,
    status: ''
  });
  const navigate = useNavigate();
 // Pentru a redirecționa după editare

  useEffect(() => {
    const fetchPackages = async () => {
      try {
        const response = await axios.get('http://localhost:8081/package/get-package');
        setPackages(response.data);
      } catch (error) {
        console.error('Error fetching packages:', error);
      }
    };

    fetchPackages();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this package?')) {
      try {
        await axios.delete(`http://localhost:8081/package/delete-package/${id}`);
        setPackages((prevPackages) => prevPackages.filter((pkg) => pkg.id !== id));
        alert('Package deleted successfully!');
      } catch (error) {
        console.error('Error deleting package:', error);
        alert('Failed to delete package.');
      }
    }
  };

  const handleEdit = (pkg) => {
    setEditPackage(pkg); // Setează pachetul care urmează să fie editat
    setFormData({
      id: pkg.id,
      courier: pkg.courier?.name || '',
      deliveryAddress: pkg.deliveryAddress,
      payOnDelivery: pkg.payOnDelivery,
      status: pkg.status
    });
    navigate('/edit-package/${pkg.id}')
  };

  const handleFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:8081/package/update-package/${formData.id}`, formData);
      alert('Package updated successfully!');
      setEditPackage(null); // Închide formularul de editare
      setPackages((prevPackages) => prevPackages.map((pkg) =>
        pkg.id === formData.id ? { ...pkg, ...formData } : pkg
      ));
    } catch (error) {
      console.error('Error updating package:', error);
      alert('Failed to update package.');
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Packages</h1>

      {editPackage && (
        <div style={styles.formContainer}>
          <h2>Edit Package</h2>
          <form onSubmit={handleFormSubmit} style={styles.form}>
            <div style={styles.formGroup}>
              <label>ID:</label>
              <input
                type="text"
                value={formData.id}
                readOnly
                style={styles.input}
              />
            </div>
            <div style={styles.formGroup}>
              <label>Courier:</label>
              <input
                type="text"
                name="courier"
                value={formData.courier}
                onChange={handleFormChange}
                style={styles.input}
              />
            </div>
            <div style={styles.formGroup}>
              <label>Delivery Address:</label>
              <input
                type="text"
                name="deliveryAddress"
                value={formData.deliveryAddress}
                onChange={handleFormChange}
                style={styles.input}
              />
            </div>
            <div style={styles.formGroup}>
              <label>Pay on Delivery:</label>
              <input
                type="checkbox"
                name="payOnDelivery"
                checked={formData.payOnDelivery}
                onChange={handleFormChange}
                style={styles.checkbox}
              />
            </div>
            <div style={styles.formGroup}>
              <label>Status:</label>
              <input
                type="text"
                name="status"
                value={formData.status}
                onChange={handleFormChange}
                style={styles.input}
              />
            </div>
            <div style={styles.formActions}>
              <button type="submit" style={styles.saveButton}>Save</button>
              <button type="button" onClick={() => setEditPackage(null)} style={styles.cancelButton}>Cancel</button>
            </div>
          </form>
        </div>
      )}

      {!editPackage && (
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>ID</th>
              <th style={styles.th}>Courier</th>
              <th style={styles.th}>Delivery Address</th>
              <th style={styles.th}>Pay on Delivery</th>
              <th style={styles.th}>Status</th>
              <th style={styles.th}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {packages.map((pkg) => (
              <tr key={pkg.id}>
                <td style={styles.td}>{pkg.id}</td>
                <td style={styles.td}>{pkg.courier?.name || 'N/A'}</td>
                <td style={styles.td}>{pkg.deliveryAddress}</td>
                <td style={styles.td}>{pkg.payOnDelivery ? 'Yes' : 'No'}</td>
                <td style={styles.td}>{pkg.status}</td>
                <td style={styles.td}>
                  <button
                    style={styles.editButton}
                    onClick={() => handleEdit(pkg)}
                  >
                    Edit
                  </button>
                  <button
                    style={styles.deleteButton}
                    onClick={() => handleDelete(pkg.id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '900px',
    margin: '50px auto',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '10px',
    backgroundColor: '#f5e9e5',
  },
  title: {
    textAlign: 'center',
    color: '#d4849a',
    marginBottom: '20px',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  th: {
    border: '1px solid #ddd',
    padding: '10px',
    textAlign: 'left',
    backgroundColor: '#d4849a',
    color: '#fff',
  },
  td: {
    border: '1px solid #ddd',
    padding: '10px',
    textAlign: 'left',
  },
  editButton: {
    padding: '5px 10px',
    marginRight: '5px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  deleteButton: {
    padding: '5px 10px',
    backgroundColor: '#f44336',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  formContainer: {
    marginBottom: '20px',
    padding: '20px',
    border: '1px solid #ddd',
    backgroundColor: '#f0f0f0',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  formGroup: {
    marginBottom: '10px',
  },
  input: {
    padding: '8px',
    fontSize: '14px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  checkbox: {
    marginTop: '5px',
  },
  formActions: {
    display: 'flex',
    justifyContent: 'space-between',
  },
  saveButton: {
    padding: '8px 15px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  cancelButton: {
    padding: '8px 15px',
    backgroundColor: '#f44336',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

export default ViewPackages;
