import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CreatePackage = ({ addPackageToList }) => {
    const [couriers, setCouriers] = useState([]); // Stocăm lista curierilor
    const [packageData, setPackageData] = useState({
        deliveryAddress: '',
        payOnDelivery: false,
        status: 'NEW',
        courierId: '', // Stocăm ID-ul curierului selectat
    });
    const [message, setMessage] = useState('');

    // Obținem lista curierilor la montarea componentei
    useEffect(() => {
        const fetchCouriers = async () => {
            try {
                const response = await axios.get('http://localhost:8081/get-courier');
                setCouriers(response.data);
            } catch (error) {
                console.error('Error fetching couriers:', error);
                setMessage('Failed to fetch couriers. Please try again.');
            }
        };

        fetchCouriers();
    }, []);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setPackageData({
            ...packageData,
            [name]: type === 'checkbox' ? checked : value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setMessage('Creating package...');
            const response = await axios.post('http://localhost:8081/package/create-package', packageData);
            setMessage(`Package created successfully: ID ${response.data.id}`);
            addPackageToList(response.data); // Actualizăm lista de pachete
            // Resetăm formularul
            setPackageData({
                deliveryAddress: '',
                payOnDelivery: false,
                status: 'NEW',
                courierId: '',
            });
        } catch (error) {
            console.error('Error creating package:', error);
            setMessage('Failed to create package. Please try again.');
        }
    };

    return (
        <div>
            <h1>Create Package</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="deliveryAddress">Delivery Address:</label>
                    <input
                        type="text"
                        id="deliveryAddress"
                        name="deliveryAddress"
                        value={packageData.deliveryAddress}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div>
                    <label htmlFor="payOnDelivery">
                        <input
                            type="checkbox"
                            id="payOnDelivery"
                            name="payOnDelivery"
                            checked={packageData.payOnDelivery}
                            onChange={handleChange}
                        />
                        Pay on Delivery
                    </label>
                </div>

                <div>
                    <label htmlFor="status">Status:</label>
                    <select
                        id="status"
                        name="status"
                        value={packageData.status}
                        onChange={handleChange}
                        required
                    >
                        <option value="NEW">NEW</option>
                        <option value="PENDING">PENDING</option>
                        <option value="DELIVERED">DELIVERED</option>
                    </select>
                </div>

                <div>
                    <label htmlFor="courierId">Courier:</label>
                    <select
                        id="courierId"
                        name="courierId"
                        value={packageData.courierId}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select a Courier</option>
                        {couriers.map((courier) => (
                            <option key={courier.id} value={courier.id}>
                                {courier.name}
                            </option>
                        ))}
                    </select>
                </div>

                <button type="submit">Create Package</button>
            </form>

            {message && <p>{message}</p>}
        </div>
    );
};

export default CreatePackage;
