import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const ViewPackages = () => {
  const [packages, setPackages] = useState([]);
  const [formData, setFormData] = useState({
    id: '',
    courierId: '',
    courier: '',
    deliveryAddress: '',
    payOnDelivery: false,
    status: '',
    createdOn: '',
  });
  const [editPackage, setEditPackage] = useState(false);
  const { id } = useParams();
  const navigate = useNavigate();

  // Fetch toate pachetele și datele unui pachet pe baza ID-ului
  useEffect(() => {
    const fetchPackages = async () => {
      try {
        const response = await axios.get('http://localhost:8081/package/get-package');
        setPackages(response.data);
      } catch (error) {
        console.error('Error fetching packages:', error);
      }
    };

    if (id) {
      // Dacă există un ID, înseamnă că utilizatorul vrea să editeze un pachet
      const fetchPackageData = async () => {
        try {
          const response = await axios.get(`http://localhost:8081/package/get-package/${id}`);
          setFormData({
            id: response.data.id,
            courierId: response.data.courier?.id || '',
            courier: response.data.courier?.name || '',
            deliveryAddress: response.data.deliveryAddress || '',
            payOnDelivery: response.data.payOnDelivery || false,
            status: response.data.status || '',
            createdOn: response.data.createdOn || '',
          });
        } catch (error) {
          console.error('Error fetching package data:', error);
        }
      };

      fetchPackageData();
      setEditPackage(true);
    } else {
      setEditPackage(false); // Dacă nu există un ID, înseamnă că suntem pe pagina de vizualizare
    }

    fetchPackages();
  }, [id]);

  // Gestionarea schimbării valorilor în formular
  const handleFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  // Funcția de trimitere a formularului pentru a actualiza un pachet
  const handleFormSubmit = async (e) => {
    e.preventDefault();

    if (!formData.courierId || !formData.deliveryAddress) {
      alert('Please fill all required fields!');
      return;
    }

    const updatedData = {
      courier: { id: formData.courierId },
      payOnDelivery: formData.payOnDelivery,
      status: formData.status,
      createdOn: formData.createdOn,
      deliveryAddress: formData.deliveryAddress,
    };

    try {
      await axios.put(`http://localhost:8081/package/update-package/${formData.id}`, updatedData);
      alert('Package updated successfully!');
      setEditPackage(false);

      // Actualizarea pachetelor în aplicație
      setPackages((prevPackages) =>
        prevPackages.map((pkg) => (pkg.id === formData.id ? { ...pkg, ...formData } : pkg))
      );
      navigate('/packages'); // Navigăm înapoi la lista de pachete
    } catch (error) {
      console.error('Error updating package:', error);
      alert('Failed to update package.');
    }
  };

  // Formularul pentru editare pachet
  const renderEditForm = () => (
    <form onSubmit={handleFormSubmit} style={styles.form}>
      <div style={styles.formGroup}>
        <label>ID:</label>
        <input
          type="text"
          name="id"
          value={formData.id}
          readOnly
          style={styles.input}
        />
      </div>
      <div style={styles.formGroup}>
        <label>Courier:</label>
        <input
          type="text"
          name="courier"
          value={formData.courier}
          onChange={handleFormChange}
          style={styles.input}
        />
      </div>
      <div style={styles.formGroup}>
        <label>Delivery Address:</label>
        <input
          type="text"
          name="deliveryAddress"
          value={formData.deliveryAddress}
          onChange={handleFormChange}
          style={styles.input}
        />
      </div>
      <div style={styles.formGroup}>
        <label>Pay on Delivery:</label>
        <input
          type="checkbox"
          name="payOnDelivery"
          checked={formData.payOnDelivery}
          onChange={handleFormChange}
          style={styles.checkbox}
        />
      </div>
      <div style={styles.formGroup}>
        <label>Status:</label>
        <input
          type="text"
          name="status"
          value={formData.status}
          onChange={handleFormChange}
          style={styles.input}
        />
      </div>
      <div style={styles.formActions}>
        <button type="submit" style={styles.saveButton}>Save</button>
        <button type="button" onClick={() => navigate('/packages')} style={styles.cancelButton}>Cancel</button>
      </div>
    </form>
  );

  // Renderizarea listei de pachete
  const renderPackageTable = () => (
    <table style={styles.table}>
      <thead>
        <tr>
          <th style={styles.th}>ID</th>
          <th style={styles.th}>Courier</th>
          <th style={styles.th}>Delivery Address</th>
          <th style={styles.th}>Pay on Delivery</th>
          <th style={styles.th}>Status</th>
          <th style={styles.th}>Actions</th>
        </tr>
      </thead>
      <tbody>
        {packages.map((pkg) => (
          <tr key={pkg.id}>
            <td style={styles.td}>{pkg.id}</td>
            <td style={styles.td}>{pkg.courier?.name || 'N/A'}</td>
            <td style={styles.td}>{pkg.deliveryAddress}</td>
            <td style={styles.td}>{pkg.payOnDelivery ? 'Yes' : 'No'}</td>
            <td style={styles.td}>{pkg.status}</td>
            <td style={styles.td}>
              <button
                style={styles.editButton}
                onClick={() => navigate(`/edit-package/${pkg.id}`)}
              >
                Edit
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>{editPackage ? 'Edit Package' : 'Packages'}</h1>

      {editPackage ? renderEditForm() : renderPackageTable()}
    </div>
  );
};

// Stiluri pentru componentă (folosite în codul de mai sus)
const styles = {
  container: {
    maxWidth: '900px',
    margin: '50px auto',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '10px',
    backgroundColor: '#f5e9e5',
  },
  title: {
    textAlign: 'center',
    color: '#d4849a',
    marginBottom: '20px',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  th: {
    border: '1px solid #ddd',
    padding: '10px',
    textAlign: 'left',
    backgroundColor: '#d4849a',
    color: '#fff',
  },
  td: {
    border: '1px solid #ddd',
    padding: '10px',
    textAlign: 'left',
  },
  editButton: {
    padding: '5px 10px',
    marginRight: '5px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  formGroup: {
    marginBottom: '10px',
  },
  input: {
    padding: '8px',
    fontSize: '14px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  checkbox: {
    marginTop: '5px',
  },
  formActions: {
    display: 'flex',
    justifyContent: 'space-between',
  },
  saveButton: {
    padding: '8px 15px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  cancelButton: {
    padding: '8px 15px',
    backgroundColor: '#f44336',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

export default ViewPackages;
