import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ViewMyPackages = () => {
  const [packages, setPackages] = useState([]);
  const [editingPackage, setEditingPackage] = useState(null); // Stocăm pachetul ce se editează

  const fetchPackages = async () => {
    try {
      const response = await axios.get('http://localhost:8081/package/get-package');
      setPackages(response.data);
    } catch (error) {
      console.error('Error fetching packages:', error);
    }
  };

  useEffect(() => {
    fetchPackages();
  }, []);

  // Funcția de editare pachet
  const handleEdit = (pkg) => {
    setEditingPackage(pkg);
  };

  // Funcția de ștergere pachet
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8081/package/delete-package/${id}`);
      setPackages(packages.filter(pkg => pkg.id !== id)); // Eliminăm pachetul din listă
      alert('Package deleted successfully');
    } catch (error) {
      console.error('Error deleting package:', error);
      alert('Failed to delete package');
    }
  };

  // Funcția de actualizare a pachetului
  const handleUpdate = async (updatedPackage) => {
    try {
      await axios.put(`http://localhost:8081/package/update-package/${updatedPackage.id}`, updatedPackage);
      setPackages(packages.map(pkg => pkg.id === updatedPackage.id ? updatedPackage : pkg)); // Actualizăm lista de pachete
      setEditingPackage(null); // Închidem formularul de editare
      alert('Package updated successfully');
    } catch (error) {
      console.error('Error updating package:', error);
      alert('Failed to update package');
    }
  };

  return (
    <div>
      <h1 style={styles.title}>View My Packages</h1>
      <button onClick={fetchPackages} style={styles.refreshButton}>Refresh</button>

      {/* Formularul de editare dacă se editează un pachet */}
      {editingPackage && (
        <div style={styles.editForm}>
          <h2>Edit Package</h2>
          <label>Delivery Address:</label>
          <input
            type="text"
            value={editingPackage.deliveryAddress}
            onChange={(e) => setEditingPackage({ ...editingPackage, deliveryAddress: e.target.value })}
          />
          <label>Status:</label>
          <select
            value={editingPackage.status}
            onChange={(e) => setEditingPackage({ ...editingPackage, status: e.target.value })}
          >
            <option value="NEW">NEW</option>
            <option value="PENDING">PENDING</option>
            <option value="DELIVERED">DELIVERED</option>
          </select>
          <button onClick={() => handleUpdate(editingPackage)} style={styles.button}>Update</button>
          <button onClick={() => setEditingPackage(null)} style={styles.button}>Cancel</button>
        </div>
      )}

      {/* Tabelul cu pachete */}
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.th}>Package ID</th>
            <th style={styles.th}>Courier Name</th>
            <th style={styles.th}>Created On</th>
            <th style={styles.th}>Delivery Address</th>
            <th style={styles.th}>Status</th>
            <th style={styles.th}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {packages.map((pkg) => (
            <tr key={pkg.id} style={styles.row}>
              <td style={styles.td}>{pkg.id}</td>
              <td style={styles.td}>{pkg.courier ? pkg.courier.name : 'N/A'}</td>
              <td style={styles.td}>{pkg.createdOn}</td>
              <td style={styles.td}>{pkg.deliveryAddress}</td>
              <td style={styles.td}>{pkg.status}</td>
              <td style={styles.td}>
                <button onClick={() => handleEdit(pkg)} style={styles.editButton}>Edit</button>
                <button onClick={() => handleDelete(pkg.id)} style={styles.deleteButton}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  title: {
    textAlign: 'center',
  },
  refreshButton: {
    margin: '10px 0',
    padding: '10px',
    backgroundColor: '#007BFF',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  table: {
    width: '80%',
    margin: 'auto',
    borderCollapse: 'collapse',
  },
  th: {
    backgroundColor: '#f4f4f4',
    border: '1px solid #ddd',
    padding: '10px',
    textAlign: 'left',
  },
  td: {
    border: '1px solid #ddd',
    padding: '10px',
  },
  row: {
    '&:hover': {
      backgroundColor: '#f1f1f1',
    },
  },
  editForm: {
    padding: '20px',
    border: '1px solid #ddd',
    marginBottom: '20px',
    backgroundColor: '#f9f9f9',
  },
  button: {
    padding: '10px 15px',
    borderRadius: '4px',
    border: 'none',
    backgroundColor: '#d4849a',
    color: '#fff',
    fontWeight: 'bold',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
  editButton: {
    marginRight: '10px',
    backgroundColor: '#f0ad4e',
    color: '#fff',
  },
  deleteButton: {
    backgroundColor: '#d9534f',
    color: '#fff',
  },
};

export default ViewMyPackages;
