import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ViewMyPackages = () => {
    const [packages, setPackages] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        // Fetch packages from the backend
        const fetchPackages = async () => {
            try {
                const response = await axios.get('http://localhost:8081/package/get-all-packages');
                setPackages(response.data);
            } catch (err) {
                console.error('Error fetching packages:', err);
                setError('Failed to fetch packages. Please try again later.');
            }
        };

        fetchPackages();
    }, []);

    return (
        <div>
            <h1>View My Packages</h1>

            {/* Error Message */}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            {/* Package List */}
            {packages.length > 0 ? (
                <table border="1" style={{ width: '100%', textAlign: 'left', marginTop: '20px' }}>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Delivery Address</th>
                            <th>Pay on Delivery</th>
                            <th>Status</th>
                            <th>Courier ID</th>
                            <th>Created On</th>
                        </tr>
                    </thead>
                    <tbody>
                        {packages.map((pkg) => (
                            <tr key={pkg.id}>
                                <td>{pkg.id}</td>
                                <td>{pkg.deliveryAddress}</td>
                                <td>{pkg.payOnDelivery ? 'Yes' : 'No'}</td>
                                <td>{pkg.status}</td>
                                <td>{pkg.courier ? pkg.courier.id : 'N/A'}</td>
                                <td>{new Date(pkg.createdOn).toLocaleString()}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No packages available.</p>
            )}
        </div>
    );
};

export default ViewMyPackages;
