import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const ViewPackages = () => {
  const [packages, setPackages] = useState([]);
  const [formData, setFormData] = useState({
    id: '',
    courier: '',
    deliveryAddress: '',
    payOnDelivery: false,
    status: ''
  });
  const [editPackage, setEditPackage] = useState(false);  // Folosim o variabilă de stare pentru a gestiona editarea
  const { id } = useParams();  // Prindem ID-ul din URL (dacă există)
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPackages = async () => {
      try {
        const response = await axios.get('http://localhost:8081/package/get-package');
        setPackages(response.data);
      } catch (error) {
        console.error('Error fetching packages:', error);
      }
    };

    if (id) {
      // Dacă avem un ID (editare), încărcăm datele pentru acest pachet
      const fetchPackageData = async () => {
        try {
          const response = await axios.get(`http://localhost:8081/package/get-package/${id}`);
          setFormData({
            id: response.data.id,
            courier: response.data.courier?.name || '',
            deliveryAddress: response.data.deliveryAddress,
            payOnDelivery: response.data.payOnDelivery,
            status: response.data.status
          });
        } catch (error) {
          console.error('Error fetching package data:', error);
        }
      };

      fetchPackageData();
      setEditPackage(true);  // Activăm modul de editare
    } else {
      setEditPackage(false);  // Dacă nu avem ID, suntem în modul de vizualizare
    }

    fetchPackages();
  }, [id]);  // Se rulează și când ID-ul se schimbă

  const handleFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:8081/package/update-package/${id}`);
      alert('Package updated successfully!');
      setEditPackage(false); // Ieși din modul de editare
      setPackages((prevPackages) =>
        prevPackages.map((pkg) => (pkg.id === formData.id ? { ...pkg, ...formData } : pkg))
      );
      navigate('/packages');  // După editare, navigăm înapoi la lista pachetelor
    } catch (error) {
      console.error('Error updating package:', error);
      alert('Failed to update package.');
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>{editPackage ? 'Edit Package' : 'Packages'}</h1>

      {editPackage ? (
        <form onSubmit={handleFormSubmit} style={styles.form}>
          <div style={styles.formGroup}>
            <label>ID:</label>
            <input
              type="text"
              name="id"
              value={formData.id}
              readOnly
              style={styles.input}
            />
          </div>
          <div style={styles.formGroup}>
            <label>Courier:</label>
            <input
              type="text"
              name="courier"
              value={formData.courier}
              onChange={handleFormChange}
              style={styles.input}
            />
          </div>
          <div style={styles.formGroup}>
            <label>Delivery Address:</label>
            <input
              type="text"
              name="deliveryAddress"
              value={formData.deliveryAddress}
              onChange={handleFormChange}
              style={styles.input}
            />
          </div>
          <div style={styles.formGroup}>
            <label>Pay on Delivery:</label>
            <input
              type="checkbox"
              name="payOnDelivery"
              checked={formData.payOnDelivery}
              onChange={handleFormChange}
              style={styles.checkbox}
            />
          </div>
          <div style={styles.formGroup}>
            <label>Status:</label>
            <input
              type="text"
              name="status"
              value={formData.status}
              onChange={handleFormChange}
              style={styles.input}
            />
          </div>
          <div style={styles.formActions}>
            <button type="submit" style={styles.saveButton}>Save</button>
            <button type="button" onClick={() => navigate('/packages')} style={styles.cancelButton}>Cancel</button>
          </div>
        </form>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>ID</th>
              <th style={styles.th}>Courier</th>
              <th style={styles.th}>Delivery Address</th>
              <th style={styles.th}>Pay on Delivery</th>
              <th style={styles.th}>Status</th>
              <th style={styles.th}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {packages.map((pkg) => (
              <tr key={pkg.id}>
                <td style={styles.td}>{pkg.id}</td>
                <td style={styles.td}>{pkg.courier?.name || 'N/A'}</td>
                <td style={styles.td}>{pkg.deliveryAddress}</td>
                <td style={styles.td}>{pkg.payOnDelivery ? 'Yes' : 'No'}</td>
                <td style={styles.td}>{pkg.status}</td>
                <td style={styles.td}>
                  <button
                    style={styles.editButton}
                    onClick={() => navigate(`/edit-package/${pkg.id}`)}
                  >
                    Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

// Stiluri pentru componentă (folosite în codul de mai sus)
const styles = {
  container: {
    maxWidth: '900px',
    margin: '50px auto',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '10px',
    backgroundColor: '#f5e9e5',
  },
  title: {
    textAlign: 'center',
    color: '#d4849a',
    marginBottom: '20px',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  th: {
    border: '1px solid #ddd',
    padding: '10px',
    textAlign: 'left',
    backgroundColor: '#d4849a',
    color: '#fff',
  },
  td: {
    border: '1px solid #ddd',
    padding: '10px',
    textAlign: 'left',
  },
  editButton: {
    padding: '5px 10px',
    marginRight: '5px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  formGroup: {
    marginBottom: '10px',
  },
  input: {
    padding: '8px',
    fontSize: '14px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  checkbox: {
    marginTop: '5px',
  },
  formActions: {
    display: 'flex',
    justifyContent: 'space-between',
  },
  saveButton: {
    padding: '8px 15px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  cancelButton: {
    padding: '8px 15px',
    backgroundColor: '#f44336',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

export default ViewPackages;
