import React, { useState, useEffect } from 'react';

const AssignPackage = () => {
  const [packages, setPackages] = useState([]);
  const [couriers, setCouriers] = useState([]);
  const [selectedPackageId, setSelectedPackageId] = useState('');
  const [selectedCourierId, setSelectedCourierId] = useState('');

  useEffect(() => {
    fetch('http://localhost:8081/package/get-package')
      .then((response) => response.json())
      .then((data) => {
        const unassigned = data.filter((pkg) => pkg.courier === null);
        setPackages(unassigned);
      })
      .catch((error) => console.error('Error fetching packages:', error));

    fetch('http://localhost:8081/get-courier')
      .then((response) => response.json())
      .then((data) => setCouriers(data))
      .catch((error) => console.error('Error fetching couriers:', error));
  }, []);

  const assignPackage = () => {
    if (!selectedPackageId || !selectedCourierId) {
      alert('Please select a package and a courier!');
      return;
    }

    const updatedPackage = {
      courier: { id: parseInt(selectedCourierId) },
      status: 'ASSIGNED',
    };

    fetch(`http://localhost:8081/package/update-package/${selectedPackageId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updatedPackage),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to assign package');
        }
        return response.json();
      })
      .then(() => {
        alert('Package assigned successfully!');
        // Actualizează lista de pachete
        setPackages(packages.filter((pkg) => pkg.id !== parseInt(selectedPackageId)));
      })
      .catch((error) => console.error('Error assigning package:', error));
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Assign Package to Courier</h2>
      <div style={styles.formGroup}>
        <label style={styles.label}>Select Package:</label>
        <select
          value={selectedPackageId}
          onChange={(e) => setSelectedPackageId(e.target.value)}
          style={styles.select}
        >
          <option value="">-- Select Package --</option>
          {packages.map((pkg) => (
            <option key={pkg.id} value={pkg.id}>
              {pkg.id} - {pkg.deliveryAddress}
            </option>
          ))}
        </select>
      </div>
      <div style={styles.formGroup}>
        <label style={styles.label}>Select Courier:</label>
        <select
          value={selectedCourierId}
          onChange={(e) => setSelectedCourierId(e.target.value)}
          style={styles.select}
        >
          <option value="">-- Select Courier --</option>
          {couriers.map((courier) => (
            <option key={courier.id} value={courier.id}>
              {courier.name} (ID: {courier.id})
            </option>
          ))}
        </select>
      </div>
      <button onClick={assignPackage} style={styles.button}>
        Assign Package
      </button>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
  },
  heading: {
    color: '#d39db7',
    marginBottom: '20px',
  },
  formGroup: {
    marginBottom: '15px',
  },
  label: {
    marginRight: '10px',
  },
  select: {
    padding: '5px',
    marginRight: '10px',
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#d39db7',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

export default AssignPackage;
