import React, { useState, useEffect } from 'react';

const AssignPackage = () => {
  const [packages, setPackages] = useState([]);
  const [couriers, setCouriers] = useState([]);
  const [selectedPackageId, setSelectedPackageId] = useState('');
  const [selectedCourierId, setSelectedCourierId] = useState('');

  useEffect(() => {
    // Fetch all packages and filter unassigned ones
    fetch('http://localhost:8081/package/get-package')
      .then((response) => response.json())
      .then((data) => {
        const unassigned = data.filter((pkg) => pkg.courier === null);
        setPackages(unassigned);
      })
      .catch((error) => console.error('Error fetching packages:', error));

    // Fetch all couriers
    fetch('http://localhost:8081/get-courier')
      .then((response) => response.json())
      .then((data) => setCouriers(data))
      .catch((error) => console.error('Error fetching couriers:', error));
  }, []);

  const assignPackage = () => {
    if (!selectedPackageId || !selectedCourierId) {
      alert('Please select a package and a courier!');
      return;
    }

    const updatedPackage = {
      courier: { id: parseInt(selectedCourierId, 10) },
      status: 'ASSIGNED',
    };

    fetch(`http://localhost:8081/package/update-package/${selectedPackageId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updatedPackage),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to assign package');
        }
        return response.json();
      })
      .then(() => {
        alert('Package assigned successfully!');
        // Remove the assigned package from the list
        setPackages(packages.filter((pkg) => pkg.id !== parseInt(selectedPackageId, 10)));
        setSelectedPackageId('');
        setSelectedCourierId('');
      })
      .catch((error) => console.error('Error assigning package:', error));
  };

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        backgroundColor: '#f9f9f9',
      }}
    >
      <div
        style={{
          backgroundColor: '#ffffff',
          padding: '30px',
          borderRadius: '10px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          textAlign: 'center',
          width: '400px',
        }}
      >
        <h2 style={{ color: '#d39db7', marginBottom: '20px' }}>Assign Package to Courier</h2>
        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px' }}>Select Package:</label>
          <select
            value={selectedPackageId}
            onChange={(e) => setSelectedPackageId(e.target.value)}
            style={{
              width: '100%',
              padding: '10px',
              borderRadius: '5px',
              border: '1px solid #ccc',
            }}
          >
            <option value="">-- Select Package --</option>
            {packages.map((pkg) => (
              <option key={pkg.id} value={pkg.id}>
                {pkg.id} - {pkg.deliveryAddress}
              </option>
            ))}
          </select>
        </div>
        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px' }}>Select Courier:</label>
          <select
            value={selectedCourierId}
            onChange={(e) => setSelectedCourierId(e.target.value)}
            style={{
              width: '100%',
              padding: '10px',
              borderRadius: '5px',
              border: '1px solid #ccc',
            }}
          >
            <option value="">-- Select Courier --</option>
            {couriers.map((courier) => (
              <option key={courier.id} value={courier.id}>
                {courier.name} (ID: {courier.id})
              </option>
            ))}
          </select>
        </div>
        <button
          onClick={assignPackage}
          style={{
            width: '100%',
            padding: '10px',
            backgroundColor: '#d39db7',
            color: '#ffffff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
        >
          Assign Package
        </button>
      </div>
    </div>
  );
};

export default AssignPackage;
