import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AssignPackage = () => {
  const [packages, setPackages] = useState([]);
  const [couriers, setCouriers] = useState([]);
  const [selectedPackage, setSelectedPackage] = useState(null);
  const [selectedCourier, setSelectedCourier] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [pkgResponse, courierResponse] = await Promise.all([
          axios.get('http://localhost:8080/package/get-package'),
          axios.get('http://localhost:8080/get-courier')
        ]);
        setPackages(pkgResponse.data.filter(pkg => !pkg.courier));
        setCouriers(courierResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleAssign = async () => {
    try {
      await axios.put(`http://localhost:8080/package/update-package/${selectedPackage.id}`, {
        ...selectedPackage,
        courier: { id: selectedCourier.id }
      });
      alert('Package Assigned');
    } catch (error) {
      console.error('Error assigning package:', error);
      alert('Failed to assign package');
    }
  };

  return (
    <div>
      <h2>Assign Package</h2>
      <div>
        <label>Select Package:</label>
        <select onChange={(e) => setSelectedPackage(packages.find(pkg => pkg.id === parseInt(e.target.value)))}>
          {packages.map((pkg) => (
            <option key={pkg.id} value={pkg.id}>
              {pkg.deliveryAddress} - {pkg.status}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label>Select Courier:</label>
        <select onChange={(e) => setSelectedCourier(couriers.find(courier => courier.id === parseInt(e.target.value)))}>
          {couriers.map((courier) => (
            <option key={courier.id} value={courier.id}>
              {courier.name}
            </option>
          ))}
        </select>
      </div>
      <button onClick={handleAssign}>Assign Package</button>
    </div>
  );
};

export default AssignPackage;
