import React, { useState } from 'react';
import axios from 'axios';

const CreatePackage = ({ addPackageToList }) => {
    const [packageData, setPackageData] = useState({
        deliveryAddress: '',
        payOnDelivery: false,
        status: 'NEW', // Status implicit
        courierId: '', // Opțional
    });
    const [message, setMessage] = useState('');

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setPackageData({
            ...packageData,
            [name]: type === 'checkbox' ? checked : value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Verificăm datele trimise înainte de a le trimite pe server
            console.log('Sending data:', packageData);

            const response = await axios.post('http://localhost:8081/package/create-package', {
                deliveryAddress: packageData.deliveryAddress,
                payOnDelivery: packageData.payOnDelivery,
                status: packageData.status,
                courierId: packageData.courierId ? packageData.courierId : null, // trimitem null daca nu este specificat
            });

            console.log('Response:', response.data);

            setMessage(`Package created successfully: ID ${response.data.id}`);
            addPackageToList(response.data); // Adăugăm pachetul la lista globală

            // Resetăm formularul
            setPackageData({
                deliveryAddress: '',
                payOnDelivery: false,
                status: 'NEW',
                courierId: '',
            });
        } catch (error) {
            // Logam eroarea detaliată
            console.error('Error creating package:', error);
            if (error.response) {
                // Server-ul a răspuns cu un cod de eroare
                console.error('Error response:', error.response.data);
                setMessage(`Failed to create package: ${error.response.data.message || error.response.statusText}`);
            } else if (error.request) {
                // Nu am primit un răspuns de la server
                console.error('Error request:', error.request);
                setMessage('Failed to create package. No response from server.');
            } else {
                // Alt tip de eroare
                setMessage('Failed to create package. Please try again.');
            }
        }
    };

    return (
        <div>
            <h1>Create Package</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="deliveryAddress">Delivery Address:</label>
                    <input
                        type="text"
                        id="deliveryAddress"
                        name="deliveryAddress"
                        value={packageData.deliveryAddress}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div>
                    <label htmlFor="payOnDelivery">
                        <input
                            type="checkbox"
                            id="payOnDelivery"
                            name="payOnDelivery"
                            checked={packageData.payOnDelivery}
                            onChange={handleChange}
                        />
                        Pay on Delivery
                    </label>
                </div>

                <div>
                    <label htmlFor="status">Status:</label>
                    <select
                        id="status"
                        name="status"
                        value={packageData.status}
                        onChange={handleChange}
                        required
                    >
                        <option value="NEW">NEW</option>
                        <option value="PENDING">PENDING</option>
                        <option value="DELIVERED">DELIVERED</option>
                    </select>
                </div>

                <div>
                    <label htmlFor="courierId">Courier ID (Optional):</label>
                    <input
                        type="number"
                        id="courierId"
                        name="courierId"
                        value={packageData.courierId}
                        onChange={handleChange}
                    />
                </div>

                <button type="submit">Create Package</button>
            </form>

            {message && <p>{message}</p>}
        </div>
    );
};

export default CreatePackage;
