import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CreatePackage = ({ addPackageToList }) => {
    const [couriers, setCouriers] = useState([]);
    const [packageData, setPackageData] = useState({
        deliveryAddress: '',
        payOnDelivery: false,
        status: 'NEW',
        courier: null, // Modificăm courierId în courier
    });
    const [message, setMessage] = useState('');

    useEffect(() => {
        const fetchCouriers = async () => {
            try {
                const response = await axios.get('http://localhost:8081/get-courier');
                setCouriers(response.data);
            } catch (error) {
                console.error('Error fetching couriers:', error);
                setMessage('Failed to fetch couriers. Please try again.');
            }
        };

        fetchCouriers();
    }, []);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setPackageData({
            ...packageData,
            [name]: type === 'checkbox' ? checked : value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setMessage('Creating package...');
            // Trimitem obiectul complet pentru courier (nu doar id-ul)
            const response = await axios.post('http://localhost:8081/package/create-package', {
                ...packageData,
                courier: {
                    id: packageData.courier, // Trimitem ID-ul curierului ca parte din obiectul courier
                }
            });
            setMessage(`Package created successfully: ID ${response.data.id}`);
            addPackageToList(response.data);
            setPackageData({
                deliveryAddress: '',
                payOnDelivery: false,
                status: 'NEW',
                courier: '', // Resetăm courier-ul după crearea pachetului
            });
        } catch (error) {
            console.error('Error creating package:', error);
            setMessage('Failed to create package. Please try again.');
        }
    };

    return (
        <div style={styles.container}>
            <h1 style={styles.title}>Create Package</h1>
            <form style={styles.form} onSubmit={handleSubmit}>
                <div style={styles.field}>
                    <label htmlFor="deliveryAddress" style={styles.label}>Delivery Address:</label>
                    <input
                        type="text"
                        id="deliveryAddress"
                        name="deliveryAddress"
                        value={packageData.deliveryAddress}
                        onChange={handleChange}
                        required
                        style={styles.input}
                    />
                </div>

                <div style={styles.field}>
                    <label style={styles.checkboxLabel}>
                        <input
                            type="checkbox"
                            id="payOnDelivery"
                            name="payOnDelivery"
                            checked={packageData.payOnDelivery}
                            onChange={handleChange}
                            style={styles.checkbox}
                        />
                        Pay on Delivery
                    </label>
                </div>

                <div style={styles.field}>
                    <label htmlFor="status" style={styles.label}>Status:</label>
                    <select
                        id="status"
                        name="status"
                        value={packageData.status}
                        onChange={handleChange}
                        required
                        style={styles.select}
                    >
                        <option value="NEW">NEW</option>
                        <option value="PENDING">PENDING</option>
                        <option value="DELIVERED">DELIVERED</option>
                    </select>
                </div>

                <div style={styles.field}>
                    <label htmlFor="courier" style={styles.label}>Courier:</label>
                    <select
                        id="courier"
                        name="courier"
                        value={packageData.courier}
                        onChange={handleChange}
                        required
                        style={styles.select}
                    >
                        <option value="">Select a Courier</option>
                        {couriers.map((courier) => (
                            <option key={courier.id} value={courier.id}>
                                {courier.name}
                            </option>
                        ))}
                    </select>
                </div>

                <button type="submit" style={styles.button}>Create Package</button>
            </form>

            {message && <p style={styles.message}>{message}</p>}
        </div>
    );
};

const styles = {
    container: {
        maxWidth: '500px',
        margin: '40px auto',
        padding: '20px',
        backgroundColor: '#fdf7f0',
        borderRadius: '8px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        textAlign: 'center',
    },
    title: {
        color: '#d4849a',
        fontSize: '24px',
        marginBottom: '20px',
    },
    form: {
        display: 'flex',
        flexDirection: 'column',
        gap: '15px',
    },
    field: {
        textAlign: 'left',
    },
    label: {
        display: 'block',
        marginBottom: '5px',
        fontWeight: 'bold',
        color: '#6b615b',
    },
    input: {
        width: '100%',
        padding: '10px',
        borderRadius: '4px',
        border: '1px solid #d4cfc4',
        backgroundColor: '#fff',
    },
    select: {
        width: '100%',
        padding: '10px',
        borderRadius: '4px',
        border: '1px solid #d4cfc4',
        backgroundColor: '#fff',
    },
    checkboxLabel: {
        fontWeight: 'bold',
        color: '#6b615b',
    },
    checkbox: {
        marginRight: '10px',
    },
    button: {
        padding: '10px 15px',
        borderRadius: '4px',
        border: 'none',
        backgroundColor: '#d4849a',
        color: '#fff',
        fontWeight: 'bold',
        cursor: 'pointer',
        transition: 'background-color 0.3s',
    },
    message: {
        marginTop: '20px',
        color: '#6b615b',
    },
};

export default CreatePackage;
