import React, { useState } from 'react';
import axios from 'axios';

function CreatePackage() {
    // State pentru a gestiona datele formularului
    const [packageData, setPackageData] = useState({
        name: '',
        weight: '',
        destination: '',
    });

    // State pentru a gestiona erorile
    const [error, setError] = useState('');

    // Functia care actualizeaza state-ul in functie de inputul utilizatorului
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setPackageData((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    // Functia care trimite cererea POST catre backend
    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Trimitem cererea POST catre backend
            const response = await axios.post('http://localhost:8081/package/create-package', packageData);
            console.log('Package created:', response.data);
            alert('Package created successfully!');
            setPackageData({
                name: '',
                weight: '',
                destination: '',
            });
        } catch (error) {
            console.error('There was an error!', error);
            setError('Error creating package. Please try again.');
        }
    };

    return (
        <div>
            <h2>Create Package</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={packageData.name}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div>
                    <label>Weight:</label>
                    <input
                        type="number"
                        name="weight"
                        value={packageData.weight}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div>
                    <label>Destination:</label>
                    <input
                        type="text"
                        name="destination"
                        value={packageData.destination}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <button type="submit">Create Package</button>
            </form>
        </div>
    );
}

export default CreatePackage;
